import React, { useState } from 'react';

function App() {
  const [name, setName] = useState('');
  const [greeting, setGreeting] = useState('');
  const [error, setError] = useState('');

  const handleGetGreeting = async () => {
    // Clear previous messages
    setError('');
    setGreeting('');
    if (!name) {
      setError('Please enter your name.');
      return;
    }
    try {
      const response = await fetch(`http://localhost:8000/api/greet?name=${encodeURIComponent(name)}`);
      const data = await response.json();
      if (response.ok) {
        setGreeting(data.message);
      } else {
        setError(data.error || 'Something went wrong.');
      }
    } catch (err) {
      setError('Failed to fetch greeting.');
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="text-2xl font-bold mb-4">Get Your Greeting</h1>
        <input
          type="text"
          placeholder="Enter your name"
          className="border rounded p-2 mb-4 w-full"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white rounded p-2 hover:bg-blue-600 transition-colors w-full"
          onClick={handleGetGreeting}
        >
          Get Greeting
        </button>
        {greeting && <p className="mt-4 text-green-600">{greeting}</p>}
        {error && <p className="mt-4 text-red-600">{error}</p>}
      </div>
    </div>
  );
}

export default App;
